import React, { useContext } from "react";
import { TaskContext } from "./context/TaskContext";

const columns = [
  { key: "todo", title: "Planning", color: "border-purple-500" },
  { key: "design", title: "Design", color: "border-pink-500" },
  { key: "dev", title: "Development", color: "border-orange-500" },
  { key: "qa", title: "QA", color: "border-green-500" },
  { key: "done", title: "Release", color: "border-blue-500" },
];

const TaskBoard = () => {
  const { tasks } = useContext(TaskContext);

  return (
    <div className="grid grid-cols-5 gap-4 w-full">
      {columns.map((col) => (
        <div key={col.key} className="bg-white rounded-2xl shadow p-4">
          <h3 className={`text-lg font-bold border-b-4 pb-2 mb-3 ${col.color}`}>
            {col.title}
          </h3>
          {tasks
            .filter((task) => task.status === col.key)
            .map((task, i) => (
              <div
                key={i}
                className="bg-gray-100 p-3 rounded-lg shadow-sm mb-2"
              >
                <p className="font-medium">{task.title}</p>
                <p className="text-sm text-gray-500">{task.description}</p>
              </div>
            ))}
        </div>
      ))}
    </div>
  );
};

export default TaskBoard;
