import React, { useState } from "react";
import TaskModal from "./TaskModal";

const TaskCard = ({ task }) => {
  const [isOpen, setIsOpen] = useState(false);

  const priorities = {
    Urgent: { border: "border-red-500", badge: "bg-red-100 text-red-700" },
    High: { border: "border-yellow-500", badge: "bg-yellow-100 text-yellow-700" },
    Medium: { border: "border-blue-500", badge: "bg-blue-100 text-blue-700" },
    Low: { border: "border-gray-400", badge: "bg-gray-100 text-gray-700" },
  };
  const priority = priorities[task.priority] || priorities["Low"];

  return (
    <>
      <div
        className={`bg-white p-4 rounded-lg shadow-md border-l-4 ${priority.border} cursor-pointer`}
        onClick={() => setIsOpen(true)}
      >
        <h3 className="font-semibold">{task.title}</h3>
        {task.desc && <p className="text-sm text-gray-500">{task.desc}</p>}
        <div className="flex gap-2 mt-2">
          <span className={`text-xs px-2 py-1 rounded ${priority.badge}`}>
            {task.priority}
          </span>
          <span className="text-xs bg-gray-200 text-gray-700 px-2 py-1 rounded">
            {task.status}
          </span>
        </div>
      </div>

      {isOpen && <TaskModal task={task} onClose={() => setIsOpen(false)} />}
    </>
  );
};

export default TaskCard;
