import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { useTasks } from "../context/TaskContext";
import TaskCard from "./TaskCard";

const TaskBoard = () => {
  const { tasks, addTask, updateTask } = useTasks();
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [status, setStatus] = useState("To Do");
  const [priority, setPriority] = useState("Low");

  const columns = ["To Do", "In Progress", "Done"];
  const columnColors = {
    "To Do": "bg-orange-100",
    "In Progress": "bg-green-100",
    "Done": "bg-pink-100",
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const { draggableId, destination } = result;
    const newStatus = columns[destination.droppableId];
    updateTask(draggableId, { status: newStatus });
  };

  const handleAddTask = () => {
    if (!title.trim()) return;
    addTask({ title, desc, status, priority, tags: [], dueDate: "" });
    setTitle("");
    setDesc("");
    setStatus("To Do");
    setPriority("Low");
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-center text-blue-600 mb-6">
        Kanban Board
      </h1>

      {/* Add Task Form */}
      <div className="flex gap-3 mb-6">
        <input
          className="flex-1 border rounded-lg px-3 py-2"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          className="flex-1 border rounded-lg px-3 py-2"
          placeholder="Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
        />
        <select
          className="border rounded-lg px-3 py-2"
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          {columns.map((s) => (
            <option key={s}>{s}</option>
          ))}
        </select>
        <select
          className="border rounded-lg px-3 py-2"
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
        >
          <option>Urgent</option>
          <option>High</option>
          <option>Medium</option>
          <option>Low</option>
        </select>
        <button
          onClick={handleAddTask}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
        >
          Add
        </button>
      </div>

      {/* Kanban Columns */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {columns.map((col, index) => (
            <Droppable droppableId={`${index}`} key={col}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className={`${columnColors[col]} rounded-2xl shadow-md p-4 min-h-[500px] flex flex-col`}
                >
                  <h2 className="text-xl font-bold text-gray-700 mb-4">{col}</h2>
                  {tasks
                    .filter((t) => t.status === col)
                    .map((task, idx) => (
                      <Draggable key={task.id} draggableId={task.id} index={idx}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="mb-3"
                          >
                            <TaskCard task={task} />
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
};

export default TaskBoard;
