import React, { useState } from "react";
import TaskModal from "./TaskModal";

const TaskCard = ({ task }) => {
  const [isOpen, setIsOpen] = useState(false);

  const priorities = {
    Urgent: {
      border: "border-red-500",
      badge: "bg-red-100 text-red-700",
      label: "ðŸ”¥ Urgent",
    },
    High: {
      border: "border-yellow-500",
      badge: "bg-yellow-100 text-yellow-700",
      label: "âš¡ High",
    },
    Medium: {
      border: "border-blue-500",
      badge: "bg-blue-100 text-blue-700",
      label: "ðŸ“˜ Medium",
    },
    Low: {
      border: "border-gray-400",
      badge: "bg-gray-100 text-gray-700",
      label: "â¬‡ Low",
    },
  };

  const priority = priorities[task.priority] || priorities["Low"];

  return (
    <>
      <div
        className={`bg-white p-4 rounded-lg shadow-md border-l-4 ${priority.border} cursor-pointer transition transform hover:scale-[1.02] hover:shadow-lg`}
        onClick={() => setIsOpen(true)}
      >
        {/* Title */}
        <h3 className="font-semibold text-gray-800 text-lg mb-1">{task.title}</h3>

        {/* Description Preview */}
        {task.desc && (
          <p className="text-sm text-gray-500 line-clamp-2">{task.desc}</p>
        )}

        {/* Tags & Priority */}
        <div className="flex flex-wrap gap-2 mt-3">
          <span
            className={`text-xs px-2 py-1 rounded-full ${priority.badge} font-medium`}
          >
            {priority.label}
          </span>

          {task.tags?.map((tag, i) => (
            <span
              key={i}
              className="text-xs px-2 py-1 rounded-full bg-purple-100 text-purple-700 font-medium"
            >
              #{tag}
            </span>
          ))}
        </div>

        {/* Footer */}
        {task.dueDate && (
          <p className="text-xs text-gray-400 mt-2">ðŸ“… {task.dueDate}</p>
        )}
      </div>

      {isOpen && <TaskModal task={task} onClose={() => setIsOpen(false)} />}
    </>
  );
};

export default TaskCard;
