import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { useTasks } from "../context/TaskContext";
import TaskCard from "./TaskCard";
import { motion } from "framer-motion";

const TaskBoard = () => {
  const { tasks, addTask, updateTask } = useTasks();
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [status, setStatus] = useState("To Do");
  const [priority, setPriority] = useState("Low");
  const [tags, setTags] = useState("");
  const [dueDate, setDueDate] = useState("");

  const columns = ["To Do", "In Progress", "Done"];
  const columnColors = {
    "To Do": "bg-orange-100",
    "In Progress": "bg-green-100",
    "Done": "bg-pink-100",
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const { draggableId, destination } = result;
    const newStatus = columns[destination.droppableId];
    updateTask(draggableId, { status: newStatus });
  };

  const handleAddTask = () => {
    if (!title.trim()) return;
    addTask({
      title,
      desc,
      status,
      priority,
      tags: tags.split(",").map((t) => t.trim()).filter(Boolean),
      dueDate,
    });
    setTitle("");
    setDesc("");
    setStatus("To Do");
    setPriority("Low");
    setTags("");
    setDueDate("");
  };

  return (
    <div className="p-6">
      {/* Animated Heading */}
      <motion.h1
        className="text-4xl font-extrabold text-center mb-2 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 bg-clip-text text-transparent"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        ðŸš€ Kanban Board
      </motion.h1>

      <motion.p
        className="text-center text-gray-500 mb-4"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        Organize â€¢ Prioritize â€¢ Achieve
      </motion.p>

      <motion.div
        className="w-32 h-1 bg-gradient-to-r from-blue-500 to-pink-500 mx-auto rounded-full mb-6"
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ delay: 0.6, duration: 0.5 }}
      ></motion.div>

      {/* Add Task Form */}
      <div className="flex flex-wrap gap-3 mb-6">
        <input
          className="flex-1 border rounded-lg px-3 py-2"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          className="flex-1 border rounded-lg px-3 py-2"
          placeholder="Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
        />
        <input
          type="date"
          className="border rounded-lg px-3 py-2"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <input
          className="flex-1 border rounded-lg px-3 py-2"
          placeholder="Tags (comma separated)"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
        />
        <select
          className="border rounded-lg px-3 py-2"
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          {columns.map((s) => (
            <option key={s}>{s}</option>
          ))}
        </select>
        <select
          className="border rounded-lg px-3 py-2"
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
        >
          <option>Urgent</option>
          <option>High</option>
          <option>Medium</option>
          <option>Low</option>
        </select>
        <button
          onClick={handleAddTask}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
        >
          Add
        </button>
      </div>

      {/* Kanban Columns */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {columns.map((col, index) => (
            <Droppable droppableId={`${index}`} key={col}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className={`${columnColors[col]} rounded-2xl shadow-md p-4 min-h-[500px] flex flex-col`}
                >
                  <h2 className="text-xl font-bold text-gray-700 mb-4">{col}</h2>
                  {tasks
                    .filter((t) => t.status === col)
                    .map((task, idx) => (
                      <Draggable key={task.id} draggableId={task.id} index={idx}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="mb-3"
                          >
                            <TaskCard task={task} />
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
};

export default TaskBoard;
