import React from "react";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import Column from "../components/Column";
import { useTasks } from "../context/TaskContext";

const Board = () => {
  const { tasks, updateTask } = useTasks();

  const columns = ["To Do", "In Progress", "Done"];

  // Handle drag & drop movement
  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const { draggableId, destination } = result;
    updateTask(draggableId, { status: columns[destination.droppableId] });
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 p-6">
        {columns.map((col, index) => (
          <Droppable droppableId={`${index}`} key={col}>
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                className="bg-gray-100 rounded-2xl shadow-md p-4 min-h-[500px] flex flex-col"
              >
                <h2 className="text-xl font-bold text-gray-700 mb-4">
                  {col}
                </h2>

                {/* Render Column Component */}
                <Column
                  column={col}
                  tasks={tasks.filter((t) => t.status === col)}
                />

                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </div>
    </DragDropContext>
  );
};

export default Board;
