import React, { useMemo } from "react";
import { DndContext, DragOverlay, closestCenter, PointerSensor, useSensor, useSensors } from "@dnd-kit/core";
import { arrayMove, SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import Column from "./TaskColumn.jsx";
import TaskCard from "./TaskCard.jsx";
import { useTasks } from "../context/TaskContext.jsx";

export default function TaskBoard() {
  const { tasks, dispatch, STATUSES } = useTasks();

  const sensors = useSensors(useSensor(PointerSensor, { activationConstraint: { distance: 6 } }));

  // group by status, sorted by sort
  const columns = useMemo(() => {
    const map = Object.fromEntries(STATUSES.map(s => [s, []]));
    for (const t of tasks) {
      map[t.status] = map[t.status] || [];
      map[t.status].push(t);
    }
    for (const k of Object.keys(map)) {
      map[k].sort((a,b) => (a.sort ?? 0) - (b.sort ?? 0));
    }
    return map;
  }, [tasks, STATUSES]);

  const [activeId, setActiveId] = React.useState(null);

  function handleDragStart(event) {
    setActiveId(event.active.id);
  }

  function handleDragOver() {
    // optional: implement preview movement while dragging (left simple)
  }

  function handleDragEnd(event) {
    const { active, over } = event;
    setActiveId(null);
    if (!over) return;

    const activeTask = tasks.find(t => t.id === active.id);
    const overTask = tasks.find(t => t.id === over.id);

    // if dropped onto a column (over.id is column id) or onto a task
    const destStatus = overTask ? overTask.status : over.id;

    if (!destStatus || !STATUSES.includes(destStatus)) return;

    if (activeTask.status !== destStatus) {
      // move to new column end
      dispatch({ type: "UPDATE", id: active.id, updates: { status: destStatus, sort: (columns[destStatus].length) } });
    } else if (overTask && active.id !== over.id) {
      // reorder within same column
      const col = columns[destStatus].map(t => t.id);
      const oldIndex = col.indexOf(active.id);
      const newIndex = col.indexOf(overTask.id);
      if (oldIndex !== -1 && newIndex !== -1 && oldIndex !== newIndex) {
        const newOrder = arrayMove(col, oldIndex, newIndex);
        // write back sorts
        newOrder.forEach((id, idx) => {
          dispatch({ type: "UPDATE", id, updates: { sort: idx } });
        });
      }
    }
  }

  const activeTask = tasks.find(t => t.id === activeId);

  return (
    <>
      <div className="flex gap-6">
        <DndContext sensors={sensors} collisionDetection={closestCenter} onDragStart={handleDragStart} onDragOver={handleDragOver} onDragEnd={handleDragEnd}>
          {STATUSES.map((s) => (
            <SortableContext key={s} items={columns[s].map(t => t.id)} strategy={verticalListSortingStrategy}>
              <Column key={s} id={s} title={s === "todo" ? "To Do" : s === "inprogress" ? "In Progress" : "Done"} items={columns[s]} />
            </SortableContext>
          ))}
          <DragOverlay>{activeTask ? <TaskCard task={activeTask} dragOverlay /> : null}</DragOverlay>
        </DndContext>
      </div>
    </>
  );
}
