import React, { useState } from "react";
import { useTasks } from "../context/TaskContext";

const TaskCard = ({ task }) => {
  const { updateTask, deleteTask } = useTasks();
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(task.title);
  const [desc, setDesc] = useState(task.desc);

  const priorities = {
    Urgent: { border: "border-red-500", badge: "bg-red-100 text-red-700" },
    High: { border: "border-yellow-500", badge: "bg-yellow-100 text-yellow-700" },
    Medium: { border: "border-blue-500", badge: "bg-blue-100 text-blue-700" },
    Low: { border: "border-gray-400", badge: "bg-gray-100 text-gray-700" },
  };
  const priority = priorities[task.priority] || priorities["Low"];

  const handleSave = () => {
    updateTask(task.id, { title, desc });
    setIsEditing(false);
  };

  return (
    <div
      className={`bg-white p-4 rounded-lg shadow-md border-l-4 ${priority.border}`}
    >
      {isEditing ? (
        <>
          <input
            className="w-full border px-2 py-1 rounded mb-2"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <textarea
            className="w-full border px-2 py-1 rounded mb-2"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
          />
          <button
            onClick={handleSave}
            className="bg-green-500 text-white px-2 py-1 rounded mr-2"
          >
            Save
          </button>
          <button
            onClick={() => setIsEditing(false)}
            className="bg-gray-300 px-2 py-1 rounded"
          >
            Cancel
          </button>
        </>
      ) : (
        <>
          <h3 className="font-semibold">{task.title}</h3>
          {task.desc && <p className="text-sm text-gray-500">{task.desc}</p>}

          <div className="flex justify-between items-center mt-2">
            <span
              className={`text-xs px-2 py-1 rounded ${priority.badge}`}
            >
              {task.priority}
            </span>
            <div className="flex gap-2">
              <button
                onClick={() => setIsEditing(true)}
                className="text-blue-500 text-sm"
              >
                ‚úèÔ∏è Edit
              </button>
              <button
                onClick={() => deleteTask(task.id)}
                className="text-red-500 text-sm"
              >
                üóëÔ∏è Delete
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default TaskCard;
