import TaskCard from "./TaskCard";
import { Droppable } from "react-beautiful-dnd";

const Column = ({ column, tasks, droppableId, onDelete }) => (
  <Droppable droppableId={droppableId}>
    {(provided) => (
      <div
        ref={provided.innerRef}
        {...provided.droppableProps}
        className="bg-gray-100 p-4 rounded-xl shadow-md min-h-[300px]"
      >
        <h2 className="font-bold text-lg mb-3">{column}</h2>
        {tasks.map((task, index) => (
          <TaskCard key={task.id} task={task} index={index} onDelete={onDelete} />
        ))}
        {provided.placeholder}
      </div>
    )}
  </Droppable>
);

export default Column;
