import React, { useState } from "react";
import { useTasks } from "../context/TaskContext";

const TaskModal = ({ task, onClose }) => {
  const { updateTask, deleteTask } = useTasks();
  const [editTask, setEditTask] = useState(task);

  const handleSave = () => {
    // Split tags into array
    if (typeof editTask.tags === "string") {
      editTask.tags = editTask.tags
        .split(",")
        .map((tag) => tag.trim())
        .filter(Boolean);
    }
    updateTask(task.id, editTask);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl shadow-lg w-11/12 md:w-1/2 p-6">
        <h2 className="text-2xl font-bold mb-4">Task Details</h2>

        <input
          type="text"
          value={editTask.title}
          onChange={(e) => setEditTask({ ...editTask, title: e.target.value })}
          className="border p-2 rounded w-full mb-3"
        />
        <textarea
          value={editTask.description}
          onChange={(e) =>
            setEditTask({ ...editTask, description: e.target.value })
          }
          className="border p-2 rounded w-full mb-3"
          rows="4"
        />

        <input
          type="date"
          value={editTask.deadline || ""}
          onChange={(e) =>
            setEditTask({ ...editTask, deadline: e.target.value })
          }
          className="border p-2 rounded w-full mb-3"
        />

        <input
          type="text"
          value={Array.isArray(editTask.tags) ? editTask.tags.join(", ") : editTask.tags || ""}
          onChange={(e) => setEditTask({ ...editTask, tags: e.target.value })}
          placeholder="Tags (comma separated)"
          className="border p-2 rounded w-full mb-3"
        />

        <select
          value={editTask.status}
          onChange={(e) => setEditTask({ ...editTask, status: e.target.value })}
          className="border p-2 rounded w-full mb-3"
        >
          <option>To Do</option>
          <option>In Progress</option>
          <option>Done</option>
        </select>

        <select
          value={editTask.priority}
          onChange={(e) =>
            setEditTask({ ...editTask, priority: e.target.value })
          }
          className="border p-2 rounded w-full mb-3"
        >
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
        </select>

        <div className="flex justify-between mt-4">
          <button
            onClick={handleSave}
            className="bg-green-500 text-white px-4 py-2 rounded"
          >
            Save
          </button>
          <button
            onClick={() => deleteTask(task.id)}
            className="bg-red-500 text-white px-4 py-2 rounded"
          >
            Delete
          </button>
          <button
            onClick={onClose}
            className="bg-gray-400 text-white px-4 py-2 rounded"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default TaskModal;
